import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import useHttp from "../../hooks/use-http";
import classes from "./AvailableMeals.module.css";
import { useEffect, useState } from "react";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const { httpRequest: fetchMealsData, isLoading, error } = useHttp();
  console.log(error);

  useEffect(() => {
    const requestConfig = {
      url: "https://react-http-d9ed7-default-rtdb.europe-west1.firebasedatabase.app/meals",
    };

    const transformData = (fetchedObj) => {
      const meals = [];

      for (const key in fetchedObj) {
        meals.push({
          id: key,
          name: fetchedObj[key].name,
          description: fetchedObj[key].description,
          price: fetchedObj[key].price,
        });
      }

      setMeals(meals);
    };

    fetchMealsData(requestConfig, transformData);
  }, [fetchMealsData]);

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>{isLoading ? <p>{"Loading..."}</p> : <ul>{mealsList}</ul>}</Card>
    </section>
  );
};

export default AvailableMeals;
